def scrape_course_details(url: str) -> dict:
    """Scrape detailed information from individual course page."""
    try:
        time.sleep(1)  # Rate limiting
        response = requests.get(url, timeout=30)
        response.raise_for_status()
        soup = BeautifulSoup(response.text, "html.parser")

        details = {}

        # Extract description - try multiple sources
        description = ""

        # Try og:description meta tag first (most reliable)
        og_desc = soup.find("meta", property="og:description")
        if og_desc and og_desc.get("content"):
            description = og_desc.get("content", "").strip()

        # If not found, try course-description-container
        if not description:
            desc_container = soup.find("div", class_="course-description-container")
            if desc_container:
                paragraphs = desc_container.find_all("p")
                # Get first meaningful paragraph (skip prerequisites text)
                for p in paragraphs:
                    text = p.get_text(strip=True)
                    if text and len(text) > 50 and "prerequisite" not in text.lower() and "enrollment" not in text.lower():
                        description = text
                        break

        details["description"] = description

