        if "error" in result:
            text = f"‚ùå Error: {result['error']}"
        else:
            text = result.get("result", "No results")
            if "next_step_hint" in result:
                text += f"\n\nüí° {result['next_step_hint']}"

        return [TextContent(type="text", text=text)]

