        url = arguments["url"]
        page = arguments.get("page", 1)
        result = await fetch_url(url, page)

        # Format the result
        if "error" in result:
            text = f"‚ùå Error: {result['error']}"
        else:
            metadata = result.get("metadata", {})
            content = result.get("content", "")
            toc = result.get("table_of_contents", [])

